namespace chp;

use League\CommonMark\CommonMarkConverter;

require_once '../vendor/autoload.php';

$fileToShow = '../README.md';

$oPage =  new \Ease\WebPage(basename($fileToShow));
$container = $oPage->addItem(new \Ease\TWB\Container());


if (file_exists($fileToShow)) {
    $converter = new CommonMarkConverter();
    $container->addItem(new \Ease\Html\Div($converter->convertToHtml(file_get_contents($fileToShow)),
            ['class' => 'jumbotron']));
}

$oPage->addItem(new \Ease\Html\HrTag());
$oPage->addItem(new \Ease\Html\ATag( 'pdf.php' ,  new \Ease\Html\ImgTag('data:image/jpeg;base64,', 'Get PDF')));
$oPage->addItem(new \Ease\Html\ATag( $fileToShow ,  new \Ease\Html\ImgTag('data:image/svg+xml;base64,', 'Get MarkDown')));
                                                                           
$oPage->draw();


